.fyk {font-family: 'Yanone Kaffeesatz', sans-serif;}
.bg-blue {
  --tw-bg-opacity: 1;
  background-color:rgb(0 0 245 / var(--tw-bg-opacity, 1))
}
        </style>
<!--/add -->
